import React from "react";

const Input = () => {
  return (
    <div className="input">
      <input type="text" placeholder="Viết tin nhắn ..." />

      <div className="send">
        <img src="" />
        <input type="file" style={{ display: "none" }} id="file" />

        <label htmlFor="file">
          <img src="" />
        </label>
        <button>Send</button>
      </div>
    </div>
  );
};

export default Input;
